import React, { useState, useEffect } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import Message from '../components/Message';
import Loader from '../components/Loader';
import { getUserDetails, updateUserProfile } from '../redux/actions/userActions';
import { USER_UPDATE_PROFILE_RESET } from '../redux/constants/userConstants';

const ProfileScreen = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [department, setDepartment] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState(null);

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error, user } = userDetails;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
  const { success } = userUpdateProfile;

  useEffect(() => {
    if (!userInfo) {
      navigate('/login');
    } else {
      if (!user || !user.name || success) {
        dispatch({ type: USER_UPDATE_PROFILE_RESET });
        dispatch(getUserDetails());
      } else {
        setName(user.name);
        setEmail(user.email);
        setDepartment(user.department);
      }
    }
  }, [dispatch, navigate, userInfo, user, success]);

  const submitHandler = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage('Passwords do not match');
    } else {
      dispatch(
        updateUserProfile({
          id: user._id,
          name,
          email,
          department,
          password,
        })
      );
    }
  };

  return (
    <Row className="py-3">
      <Col md={4}>
        <div className="bg-white p-4 rounded shadow-sm border mb-4" style={{
          borderColor: 'var(--border-color)'
        }}>
          <div className="text-center mb-4">
            <h2 className="fw-bold">
              <i className="fas fa-user-edit me-2"></i>Edit Your Profile
            </h2>
            <p className="text-muted">Make changes to your profile information below and click Update</p>
          </div>
        {message && <Message variant="danger">{message}</Message>}
        {error && <Message variant="danger">{error}</Message>}
        {success && <Message variant="success">Profile Updated</Message>}
        {loading && <Loader />}
          <Form onSubmit={submitHandler} className="pb-3">
          <Form.Group controlId="name" className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="email" className="mb-3">
            <Form.Label>Email Address</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="department" className="mb-3">
            <Form.Label>Department</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter department"
              value={department}
              onChange={(e) => setDepartment(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="password" className="mb-3">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Enter password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="confirmPassword" className="mb-3">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Confirm password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            ></Form.Control>
          </Form.Group>

            <div className="d-grid gap-2 mt-4">
              <Button
                type="submit"
                variant="primary"
                size="lg"
              >
                <i className="fas fa-save me-2"></i> Save Changes
              </Button>
            </div>
          </Form>
        </div>
      </Col>
      <Col md={7} className="offset-md-1">
        <div className="bg-white p-4 rounded shadow-sm border mb-4" style={{
          borderColor: 'var(--border-color)'
        }}>
          <div className="text-center mb-4">
            <h2 className="fw-bold">
              <i className="fas fa-info-circle me-2"></i>My Information
            </h2>
            <p className="text-muted">Your current account details</p>
          </div>
          {loading ? (
            <Loader />
          ) : error ? (
            <Message variant="danger">{error}</Message>
          ) : (
            <Row className="mt-4">
              <Col md={12}>
                <div className="bg-light p-4 rounded mb-3">
                  <h4 className="fw-bold mb-4 border-bottom pb-2">
                    <i className="fas fa-user me-2"></i>Personal Details
                  </h4>
                  <p className="mb-3 fs-5">
                    <strong>Name:</strong> {user.name}
                  </p>
                  <p className="mb-3 fs-5">
                    <strong>Email:</strong> {user.email}
                  </p>
                  <p className="mb-3 fs-5">
                    <strong>Department:</strong> {user.department}
                  </p>
                  <p className="mb-0 fs-5">
                    <strong>Role:</strong> {user.role}
                  </p>
                </div>
              </Col>
            </Row>
          )}
        </div>
      </Col>
    </Row>
  );
};

export default ProfileScreen;
