/**
 * Utility functions for avatar generation
 */

/**
 * Get avatar URL from RandomUser API based on user object
 * @param {Object} user - User object with gender field
 * @returns {string} - URL to the avatar image
 */
export const getAvatarUrl = (user) => {
  // If user is an ID string (for backward compatibility)
  if (typeof user === 'string') {
    const id = user;
    // Use the last character of the ID to determine the avatar number (1-8)
    const lastChar = id.charAt(id.length - 1);
    const avatarNumber = parseInt(lastChar, 16) % 8 + 1; // Convert hex to decimal, mod 8, then add 1 (1-8)
    
    // For backward compatibility, determine gender from ID if not provided
    const gender = parseInt(lastChar, 16) % 2 === 0 ? 'men' : 'women'; // Even numbers are men, odd are women
    return `https://randomuser.me/api/portraits/${gender}/${avatarNumber}.jpg`;
  }
  
  // If user is an object with gender field
  if (user && user.gender) {
    // Map gender field to RandomUser API format
    const apiGender = user.gender === 'male' ? 'men' : 'women';
    
    // Use the last character of the ID to determine the avatar number (1-8)
    const lastChar = user._id.charAt(user._id.length - 1);
    const avatarNumber = parseInt(lastChar, 16) % 8 + 1; // Convert hex to decimal, mod 8, then add 1 (1-8)
    
    return `https://randomuser.me/api/portraits/${apiGender}/${avatarNumber}.jpg`;
  }
  
  // Fallback for when user object doesn't have gender or _id
  return 'https://api.dicebear.com/7.x/identicon/svg?seed=fallback';
};
