import React, { useEffect, useState } from 'react';

const CursorEffect = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [followerPosition, setFollowerPosition] = useState({ x: 0, y: 0 });
  const [isClicking, setIsClicking] = useState(false);

  useEffect(() => {
    // Add clickable class to interactive elements
    const addClickableClass = () => {
      const elements = document.querySelectorAll('a, button, .btn, .nav-link, .card, .form-control, .toggle-btn');
      elements.forEach(element => {
        element.classList.add('clickable');
      });
    };

    // Handle mouse movement
    const onMouseMove = (e) => {
      setPosition({ x: e.clientX, y: e.clientY });
      // Follower position with slight delay
      setTimeout(() => {
        setFollowerPosition({ x: e.clientX, y: e.clientY });
      }, 100);
    };

    // Handle mouse down
    const onMouseDown = () => {
      setIsClicking(true);
    };

    // Handle mouse up
    const onMouseUp = () => {
      setIsClicking(false);
    };

    // Add event listeners
    document.addEventListener('mousemove', onMouseMove);
    document.addEventListener('mousedown', onMouseDown);
    document.addEventListener('mouseup', onMouseUp);
    
    // Initial setup
    addClickableClass();
    
    // Hide default cursor
    document.body.style.cursor = 'none';

    // Cleanup
    return () => {
      document.removeEventListener('mousemove', onMouseMove);
      document.removeEventListener('mousedown', onMouseDown);
      document.removeEventListener('mouseup', onMouseUp);
      document.body.style.cursor = 'default';
    };
  }, []);

  return (
    <>
      <div 
        className="custom-cursor" 
        style={{ 
          left: `${position.x}px`, 
          top: `${position.y}px`,
          transform: isClicking ? 'translate(-50%, -50%) scale(0.8)' : 'translate(-50%, -50%)',
          backgroundColor: isClicking ? 'rgba(239, 124, 142, 0.9)' : 'rgba(239, 124, 142, 0.5)'
        }}
      />
      <div 
        className="custom-cursor-follower" 
        style={{ 
          left: `${followerPosition.x}px`, 
          top: `${followerPosition.y}px`,
          transform: isClicking ? 'translate(-50%, -50%) scale(0.8)' : 'translate(-50%, -50%)'
        }}
      />
    </>
  );
};

export default CursorEffect;
